import React, { useEffect, useState } from "react";
import api from "../../api/api";
import StoreCard from "../../components/StoreCard";
import AddStoreForm from "../../components/AddStoreForm";

const OwnerDashboard = () => {
  const [stores, setStores] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetchOwnerData();
  }, []);

  const fetchOwnerData = async () => {
    try {
      const storeRes = await api.get("/stores/owner/my-stores");
      const statsRes = await api.get("/dashboard/owner/stats");

      setStores(storeRes.data.data || []);
      setStats(statsRes.data || {});
    } catch (err) {
      console.error("OwnerDashboard fetch error:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = async (store) => {
    const name = prompt("Enter new name:", store.name);
    const email = prompt("Enter new email:", store.email);
    const address = prompt("Enter new address:", store.address);
    if (!name || !email || !address) return;

    try {
      await api.put(`/stores/owner/${store.id}`, { name, email, address });
      fetchOwnerData();
    } catch (err) {
      console.error("Error updating store:", err);
    }
  };

  const handleDelete = async (store) => {
    if (!window.confirm("Delete this store?")) return;
    try {
      await api.delete(`/stores/owner/${store.id}`);
      fetchOwnerData();
    } catch (err) {
      console.error("Error deleting store:", err);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>Owner Dashboard</h2>

      <div className="analytics">
        <p>My Stores: {stats?.totalStores || stores.length}</p>
        <p>Total Ratings: {stats?.totalRatings || 0}</p>
        <p>Average Rating: {stats?.avgRating?.toFixed(1) || 0} ⭐</p>
      </div>

      {/* Toggle Add Store */}
      <button onClick={() => setShowForm(!showForm)} className="btn primary">
        {showForm ? "✖ Close Form" : "➕ Add Store"}
      </button>

      {showForm && <AddStoreForm role="owner" onSuccess={fetchOwnerData} />}

      <h3>My Stores</h3>
      <div className="store-list">
        {stores.map((store) => (
          <StoreCard
            key={store.id}
            store={store}
            onEdit={() => handleEdit(store)}
            onDelete={() => handleDelete(store)}
          />
        ))}
      </div>
    </div>
  );
};

export default OwnerDashboard;
