import React, { useState, useEffect, useContext } from "react";
import api from "../../api/api";
import { AuthContext } from "../../context/AuthContext";
import RatingModal from "../../components/RatingModal";

const OwnerDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);

  const [selectedStore, setSelectedStore] = useState(null);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");

  useEffect(() => {
    fetchStores();
  }, []);

  const fetchStores = async () => {
    try {
      const response = await api.get("/stores");
      // Only show owner’s stores
      setStores(response.data.data.filter((s) => s.ownerId === user?.id));
    } catch (error) {
      console.error("Error fetching stores:", error);
    } finally {
      setLoading(false);
    }
  };

  const createStore = async (storeData) => {
    try {
      await api.post("/stores", storeData);
      fetchStores();
    } catch (error) {
      console.error("Error creating store:", error);
    }
  };

  const updateStore = async (storeId, updatedData) => {
    try {
      await api.put(`/stores/${storeId}`, updatedData);
      fetchStores();
    } catch (error) {
      console.error("Error updating store:", error);
    }
  };

  const deleteStore = async (storeId) => {
    try {
      await api.delete(`/stores/${storeId}`);
      fetchStores();
    } catch (error) {
      console.error("Error deleting store:", error);
    }
  };

  const resetModal = () => {
    setSelectedStore(null);
    setRating(5);
    setComment("");
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="dashboard-container">
      <h2>Owner Dashboard</h2>

      {/* Rating Modal (reused) */}
      <RatingModal
        store={selectedStore}
        rating={rating}
        comment={comment}
        setRating={setRating}
        setComment={setComment}
        onSubmit={() => resetModal()}
        onClose={resetModal}
        isUpdating={false}
      />

      {/* Stores Section */}
      <h3>My Stores</h3>
      <div className="stores-grid">
        {stores.map((store) => (
          <div key={store.id} className="store-card">
            <h4>{store.name}</h4>
            <p>{store.address}</p>
            <p>
              Average Rating: {store.averageRating || 0} ⭐ (
              {store.totalRatings || 0} ratings)
            </p>

            <button
              onClick={() => updateStore(store.id, { name: "Updated Name" })}
              className="btn btn-primary"
            >
              Edit
            </button>
            <button
              onClick={() => deleteStore(store.id)}
              className="btn btn-danger"
            >
              Delete
            </button>
          </div>
        ))}
      </div>

      <button
        onClick={() => createStore({ name: "New Store", address: "Somewhere" })}
        className="btn btn-success"
      >
        + Add Store
      </button>
    </div>
  );
};

export default OwnerDashboard;
