import React, { useState, useEffect, useCallback } from "react";
import api from "../../api/api";

const OwnerDashboard = () => {
  const [analytics, setAnalytics] = useState({});
  const [stores, setStores] = useState([]);
  const [allStores, setAllStores] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMyStores();
    fetchAllStores();
  }, []);

  useEffect(() => {
    if (allStores.length > 0) {
      fetchAllRatings();
    }
  }, [allStores]);

  useEffect(() => {
    if (stores.length >= 0 && ratings.length >= 0) {
      calculateAnalytics();
    }
  }, [stores, ratings]);

  const fetchMyStores = async () => {
    try {
      const response = await api.get("/stores/owner/my-stores");
      setStores(response.data.data || []);
    } catch (error) {
      console.error("Error fetching my stores:", error);
      setStores([]);
    }
  };

  const fetchAllStores = async () => {
    try {
      const response = await api.get("/stores");
      setAllStores(response.data.data || []);
    } catch (error) {
      console.error("Error fetching all stores:", error);
      setAllStores([]);
    }
  };

  const fetchAllRatings = async () => {
    try {
      // Get ratings for all stores (public endpoint)
      const ratingPromises = allStores.map(store =>
        api.get(`/ratings/store/${store.id}`)
      );
      const ratingResponses = await Promise.all(ratingPromises);
      const allRatings = ratingResponses.flatMap(response => response.data.data);
      setRatings(allRatings);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching ratings:", error);
      setLoading(false);
    }
  };

  const calculateAnalytics = () => {
    const myStoreIds = stores.map(store => store.id);
    const myRatings = ratings.filter(rating => myStoreIds.includes(rating.storeId));

    setAnalytics({
      myStores: stores.length,
      totalRatings: myRatings.length,
      averageRating: myRatings.length > 0 ? (myRatings.reduce((sum, r) => sum + r.rating, 0) / myRatings.length).toFixed(1) : 0
    });
  };

  const deleteStore = async (id) => {
    if (window.confirm("Are you sure you want to delete this store?")) {
      try {
        await api.delete(`/stores/owner/${id}`);
        fetchMyStores();
      } catch (error) {
        console.error("Error deleting store:", error);
      }
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Owner Dashboard</h2>

      {/* Analytics Section */}
      <div style={{ display: "flex", gap: "20px", marginBottom: "30px" }}>
        <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "5px", flex: 1 }}>
          <h3>My Stores</h3>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{analytics.myStores}</p>
        </div>
        <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "5px", flex: 1 }}>
          <h3>Total Ratings</h3>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{analytics.totalRatings}</p>
        </div>
        <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "5px", flex: 1 }}>
          <h3>Average Rating</h3>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{analytics.averageRating} ⭐</p>
        </div>
      </div>

      {/* My Stores Section */}
      <div style={{ marginBottom: "30px" }}>
        <h3>My Stores</h3>
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))", gap: "20px" }}>
          {stores.map(store => (
            <div key={store.id} style={{ border: "1px solid #ccc", padding: "15px", borderRadius: "5px" }}>
              <h4>{store.name}</h4>
              <p>{store.address}</p>
              <p>{store.email}</p>
              <p>Average Rating: {store.averageRating || 0} ⭐</p>
              <p>Total Ratings: {store.totalRatings || 0}</p>
              <div style={{ marginTop: "10px" }}>
                <button style={{ marginRight: "10px", padding: "5px 10px", backgroundColor: "#007bff", color: "white", border: "none", borderRadius: "3px" }}>
                  Edit
                </button>
                <button
                  onClick={() => deleteStore(store.id)}
                  style={{ padding: "5px 10px", backgroundColor: "#dc3545", color: "white", border: "none", borderRadius: "3px" }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* All Stores and Ratings Section */}
      <div>
        <h3>All Stores and Ratings</h3>
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(400px, 1fr))", gap: "20px" }}>
          {allStores.map(store => {
            const storeRatings = ratings.filter(r => r.storeId === store.id);
            return (
              <div key={store.id} style={{ border: "1px solid #ccc", padding: "15px", borderRadius: "5px" }}>
                <h4>{store.name}</h4>
                <p>{store.address}</p>
                <p>Owner: {store.owner?.name}</p>
                <p>Average Rating: {store.averageRating || 0} ⭐</p>
                <p>Total Ratings: {store.totalRatings || 0}</p>

                <div style={{ marginTop: "10px" }}>
                  <h5>Ratings:</h5>
                  {storeRatings.length > 0 ? (
                    storeRatings.slice(0, 3).map(rating => (
                      <div key={rating.id} style={{ borderTop: "1px solid #eee", paddingTop: "5px", marginTop: "5px" }}>
                        <p><strong>{rating.user?.name}:</strong> {"⭐".repeat(rating.rating)} - {rating.comment || "No comment"}</p>
                      </div>
                    ))
                  ) : (
                    <p>No ratings yet</p>
                  )}
                  {storeRatings.length > 3 && <p>... and {storeRatings.length - 3} more</p>}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default OwnerDashboard;
