import React, { useContext } from "react";
import { AuthContext } from "../context/AuthContext"; // adjust path if needed

const StoreCard = ({ store, onEdit, onDelete, onRate }) => {
  const { user } = useContext(AuthContext);

  // Check permissions
  const isOwner = user?.role === "owner" && user?.id === store.owner?.id;
  const isAdmin = user?.role === "admin";
  const isUser = user?.role === "user";

  return (
    <div className="store-card">
      <h3>{store.name}</h3>
      <p>üìç Address: {store.address || "Not available"}</p>
      <p>
        ‚≠ê Average Rating:{" "}
        {typeof store.averageRating === "number"
          ? store.averageRating.toFixed(1)
          : "No ratings yet"}{" "}
        ({store.totalRatings || 0} ratings)
      </p>
      <p>
        <strong>Owner:</strong> {store.owner?.name || "N/A"} (
        {store.owner?.email || "N/A"})
      </p>

      <div className="actions">
        {/* Users can only rate */}
        {isUser && (
          <button onClick={() => onRate(store)} className="btn rate">
            Rate
          </button>
        )}

        {/* Owners can CRUD their own stores */}
        {isOwner && (
          <>
            <button onClick={() => onEdit(store)} className="btn edit">
              Edit
            </button>
            <button onClick={() => onDelete(store)} className="btn delete">
              Delete
            </button>
          </>
        )}

        {/* Admin can CRUD all stores */}
        {isAdmin && (
          <>
            <button onClick={() => onEdit(store)} className="btn edit">
              Edit
            </button>
            <button onClick={() => onDelete(store)} className="btn delete">
              Delete
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default StoreCard;
