import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import api from "../../api/api";
import AnalyticsCard from "../../components/AnalyticsCard";
import StoreList from "../../components/StoreList";
import StoreModal from "../../components/StoreModal";

const OwnerDashboard = () => {
  const [stats, setStats] = useState({ stores: 0, ratings: 0, avgRating: 0 });
  const [loading, setLoading] = useState(true);

  // Modal state
  const [editingStore, setEditingStore] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await api.get("/dashboard/owner/stats");
        setStats(res.data.data || { stores: 0, ratings: 0, avgRating: 0 });
      } catch (err) {
        console.error("Error fetching owner stats:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchStats();
  }, []);

  const handleEdit = (store) => {
    setEditingStore(store);
    setName(store.name || "");
    setEmail(store.email || "");
    setAddress(store.address || "");
  };

  const handleSave = async () => {
    try {
      await api.put(`/stores/owner/${editingStore.id}`, {
        name,
        email,
        address,
      });
      // Optionally refresh stats or store list here
      setEditingStore(null);
    } catch (err) {
      console.error("Error updating store:", err);
      alert("Failed to update store. Please try again.");
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="dashboard-container">
      <h2>Owner Dashboard</h2>
      <div className="analytics-grid">
        <AnalyticsCard title="My Stores" value={stats.stores} />
        <AnalyticsCard title="My Ratings" value={stats.ratings} />
        <AnalyticsCard title="Avg Rating" value={`${stats.avgRating.toFixed(1)} ⭐`} />
      </div>

      <h3>My Stores</h3>
      <StoreList
        endpoint="/stores/owner/my-stores"
        showRatings
        canEdit
        onEdit={handleEdit}
      />

      <button onClick={() => navigate("/stores")} className="btn btn-success">
        + Add Store
      </button>

      <StoreModal
        store={editingStore}
        name={name}
        email={email}
        address={address}
        setName={setName}
        setEmail={setEmail}
        setAddress={setAddress}
        onSave={handleSave}
        onClose={() => setEditingStore(null)}
      />
    </div>
  );
};

export default OwnerDashboard;
