import React, { useContext } from "react";
import { AuthContext } from "../context/AuthContext"; // adjust path if needed

const StoreCard = ({ store, onEdit, onDelete, onRate }) => {
  const { user } = useContext(AuthContext);

  const isOwner = user?.role === "owner" && user?.id === store.ownerId;
  const isAdmin = user?.role === "admin";
  const isUser = user?.role === "user";

  return (
    <div className="store-card">
      <h3>{store.name}</h3>
      <p>üìç Location: {store.location}</p>
      <p>‚≠ê Rating: {store.rating?.toFixed(1) || "No ratings yet"}</p>
      <p>
        <strong>Owner:</strong> {store.ownerName || "N/A"}
      </p>

      <div className="actions">
        {/* Users can only rate */}
        {isUser && (
          <button onClick={() => onRate(store)} className="btn rate">
            Rate
          </button>
        )}

        {/* Owners can CRUD but only their stores */}
        {isOwner && (
          <>
            <button onClick={() => onEdit(store)} className="btn edit">
              Edit
            </button>
            <button onClick={() => onDelete(store)} className="btn delete">
              Delete
            </button>
          </>
        )}

        {/* Admin can CRUD all stores */}
        {isAdmin && (
          <>
            <button onClick={() => onEdit(store)} className="btn edit">
              Edit
            </button>
            <button onClick={() => onDelete(store)} className="btn delete">
              Delete
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default StoreCard;
