import React, { useState, useEffect } from "react";
import api from "../../api/api";

const AdminDashboard = () => {
  const [analytics, setAnalytics] = useState({});
  const [stores, setStores] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  // Form states
  const [showStoreForm, setShowStoreForm] = useState(false);
  const [showRatingForm, setShowRatingForm] = useState(false);
  const [storeForm, setStoreForm] = useState({ name: '', email: '', address: '', ownerId: '' });
  const [ratingForm, setRatingForm] = useState({ storeId: '', userId: '', rating: 5, comment: '' });

  useEffect(() => {
    fetchStores();
    fetchRatings();
    fetchUsers();
  }, []);

  useEffect(() => {
    if (stores.length >= 0 && ratings.length >= 0) {
      calculateAnalytics();
    }
  }, [stores, ratings]);

  const calculateAnalytics = () => {
    const averageRating = ratings.length > 0
      ? (ratings.reduce((sum, r) => sum + r.rating, 0) / ratings.length).toFixed(1)
      : 0;

    setAnalytics({
      totalStores: stores.length,
      totalRatings: ratings.length,
      averageRating: averageRating
    });
  };

  const fetchStores = async () => {
    try {
      const response = await api.get("/stores/admin/all");
      setStores(response.data.data || []);
    } catch (error) {
      console.error("Error fetching stores:", error);
      setStores([]);
    }
  };

  const fetchRatings = async () => {
    try {
      const response = await api.get("/ratings/admin/all");
      setRatings(response.data.data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching ratings:", error);
      setLoading(false);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await api.get("/auth/users");
      setUsers(response.data.data || []);
    } catch (error) {
      console.error("Error fetching users:", error);
      setUsers([]);
    }
  };

  const handleCreateStore = async (e) => {
    e.preventDefault();
    try {
      await api.post("/stores/admin", storeForm);
      setStoreForm({ name: '', email: '', address: '', ownerId: '' });
      setShowStoreForm(false);
      fetchStores();
    } catch (error) {
      console.error("Error creating store:", error);
      alert("Error creating store: " + error.response?.data?.message || error.message);
    }
  };

  const handleCreateRating = async (e) => {
    e.preventDefault();
    try {
      await api.post("/ratings/admin", ratingForm);
      setRatingForm({ storeId: '', userId: '', rating: 5, comment: '' });
      setShowRatingForm(false);
      fetchRatings();
    } catch (error) {
      console.error("Error creating rating:", error);
      alert("Error creating rating: " + error.response?.data?.message || error.message);
    }
  };

  const deleteStoreHandler = async (id) => {
    if (window.confirm("Are you sure you want to delete this store?")) {
      try {
        await api.delete(`/stores/admin/${id}`);
        fetchStores();
      } catch (error) {
        console.error("Error deleting store:", error);
      }
    }
  };

  const deleteRating = async (id) => {
    if (window.confirm("Are you sure you want to delete this rating?")) {
      try {
        await api.delete(`/ratings/admin/${id}`);
        fetchRatings();
      } catch (error) {
        console.error("Error deleting rating:", error);
      }
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Admin Dashboard</h2>

      {/* Analytics Section */}
      <div style={{ display: "flex", gap: "20px", marginBottom: "30px" }}>
        <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "5px", flex: 1 }}>
          <h3>Total Stores</h3>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{analytics.totalStores}</p>
        </div>
        <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "5px", flex: 1 }}>
          <h3>Total Ratings</h3>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{analytics.totalRatings}</p>
        </div>
        <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "5px", flex: 1 }}>
          <h3>Average Rating</h3>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{analytics.averageRating} ⭐</p>
        </div>
      </div>

      {/* Admin Actions */}
      <div style={{ marginBottom: "30px" }}>
        <h3>Admin Actions</h3>
        <div style={{ display: "flex", gap: "10px", marginBottom: "20px" }}>
          <button
            onClick={() => setShowStoreForm(!showStoreForm)}
            style={{ padding: "10px 20px", backgroundColor: "#28a745", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" }}
          >
            {showStoreForm ? "Cancel" : "Add Store"}
          </button>
          <button
            onClick={() => setShowRatingForm(!showRatingForm)}
            style={{ padding: "10px 20px", backgroundColor: "#17a2b8", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" }}
          >
            {showRatingForm ? "Cancel" : "Add Rating"}
          </button>
        </div>

        {/* Store Creation Form */}
        {showStoreForm && (
          <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "5px", marginBottom: "20px", backgroundColor: "#f9f9f9" }}>
            <h4>Create New Store</h4>
            <form onSubmit={handleCreateStore}>
              <div style={{ marginBottom: "10px" }}>
                <input
                  type="text"
                  placeholder="Store Name"
                  value={storeForm.name}
                  onChange={(e) => setStoreForm({ ...storeForm, name: e.target.value })}
                  required
                  style={{ width: "100%", padding: "8px", marginBottom: "10px", border: "1px solid #ccc", borderRadius: "3px" }}
                />
              </div>
              <div style={{ marginBottom: "10px" }}>
                <input
                  type="email"
                  placeholder="Store Email"
                  value={storeForm.email}
                  onChange={(e) => setStoreForm({ ...storeForm, email: e.target.value })}
                  required
                  style={{ width: "100%", padding: "8px", marginBottom: "10px", border: "1px solid #ccc", borderRadius: "3px" }}
                />
              </div>
              <div style={{ marginBottom: "10px" }}>
                <input
                  type="text"
                  placeholder="Store Address"
                  value={storeForm.address}
                  onChange={(e) => setStoreForm({ ...storeForm, address: e.target.value })}
                  required
                  style={{ width: "100%", padding: "8px", marginBottom: "10px", border: "1px solid #ccc", borderRadius: "3px" }}
                />
              </div>
              <div style={{ marginBottom: "10px" }}>
                <select
                  value={storeForm.ownerId}
                  onChange={(e) => setStoreForm({ ...storeForm, ownerId: e.target.value })}
                  required
                  style={{ width: "100%", padding: "8px", marginBottom: "10px", border: "1px solid #ccc", borderRadius: "3px" }}
                >
                  <option value="">Select Owner</option>
                  {users.map(user => (
                    <option key={user.id} value={user.id}>
                      {user.name} ({user.email}) - {user.role}
                    </option>
                  ))}
                </select>
              </div>
              <button
                type="submit"
                style={{ padding: "10px 20px", backgroundColor: "#28a745", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" }}
              >
                Create Store
              </button>
            </form>
          </div>
        )}

        {/* Rating Creation Form */}
        {showRatingForm && (
          <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "5px", marginBottom: "20px", backgroundColor: "#f9f9f9" }}>
            <h4>Create New Rating</h4>
            <form onSubmit={handleCreateRating}>
              <div style={{ marginBottom: "10px" }}>
                <select
                  value={ratingForm.storeId}
                  onChange={(e) => setRatingForm({ ...ratingForm, storeId: e.target.value })}
                  required
                  style={{ width: "100%", padding: "8px", marginBottom: "10px", border: "1px solid #ccc", borderRadius: "3px" }}
                >
                  <option value="">Select Store</option>
                  {stores.map(store => (
                    <option key={store.id} value={store.id}>
                      {store.name} - {store.address}
                    </option>
                  ))}
                </select>
              </div>
              <div style={{ marginBottom: "10px" }}>
                <select
                  value={ratingForm.userId}
                  onChange={(e) => setRatingForm({ ...ratingForm, userId: e.target.value })}
                  required
                  style={{ width: "100%", padding: "8px", marginBottom: "10px", border: "1px solid #ccc", borderRadius: "3px" }}
                >
                  <option value="">Select User</option>
                  {users.map(user => (
                    <option key={user.id} value={user.id}>
                      {user.name} ({user.email}) - {user.role}
                    </option>
                  ))}
                </select>
              </div>
              <div style={{ marginBottom: "10px" }}>
                <label>Rating: </label>
                <select
                  value={ratingForm.rating}
                  onChange={(e) => setRatingForm({ ...ratingForm, rating: parseInt(e.target.value) })}
                  style={{ padding: "8px", marginLeft: "10px", border: "1px solid #ccc", borderRadius: "3px" }}
                >
                  {[1, 2, 3, 4, 5].map(num => (
                    <option key={num} value={num}>{num} ⭐</option>
                  ))}
                </select>
              </div>
              <div style={{ marginBottom: "10px" }}>
                <textarea
                  placeholder="Comment (optional)"
                  value={ratingForm.comment}
                  onChange={(e) => setRatingForm({ ...ratingForm, comment: e.target.value })}
                  style={{ width: "100%", padding: "8px", marginBottom: "10px", border: "1px solid #ccc", borderRadius: "3px", minHeight: "60px" }}
                />
              </div>
              <button
                type="submit"
                style={{ padding: "10px 20px", backgroundColor: "#17a2b8", color: "white", border: "none", borderRadius: "5px", cursor: "pointer" }}
              >
                Create Rating
              </button>
            </form>
          </div>
        )}
      </div>

      {/* Stores Section */}
      <div style={{ marginBottom: "30px" }}>
        <h3>All Stores</h3>
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(300px, 1fr))", gap: "20px" }}>
          {stores.map(store => (
            <div key={store.id} style={{ border: "1px solid #ccc", padding: "15px", borderRadius: "5px" }}>
              <h4>{store.name}</h4>
              <p>{store.address}</p>
              <p>{store.email}</p>
              <p>Owner: {store.owner?.name}</p>
              <div style={{ marginTop: "10px" }}>
                <button style={{ marginRight: "10px", padding: "5px 10px", backgroundColor: "#007bff", color: "white", border: "none", borderRadius: "3px" }}>
                  Edit
                </button>
                <button
                  onClick={() => deleteStoreHandler(store.id)}
                  style={{ padding: "5px 10px", backgroundColor: "#dc3545", color: "white", border: "none", borderRadius: "3px" }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Ratings Section */}
      <div>
        <h3>All Ratings</h3>
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(400px, 1fr))", gap: "20px" }}>
          {ratings.map(rating => (
            <div key={rating.id} style={{ border: "1px solid #ccc", padding: "15px", borderRadius: "5px" }}>
              <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
                <h4>{rating.store?.name}</h4>
                <span style={{ fontSize: "18px" }}>{"⭐".repeat(rating.rating)}</span>
              </div>
              <p><strong>User:</strong> {rating.user?.name} ({rating.user?.email})</p>
              <p><strong>Comment:</strong> {rating.comment || "No comment"}</p>
              <p><strong>Date:</strong> {new Date(rating.createdAt).toLocaleDateString()}</p>
              <div style={{ marginTop: "10px" }}>
                <button style={{ marginRight: "10px", padding: "5px 10px", backgroundColor: "#007bff", color: "white", border: "none", borderRadius: "3px" }}>
                  Edit
                </button>
                <button
                  onClick={() => deleteRating(rating.id)}
                  style={{ padding: "5px 10px", backgroundColor: "#dc3545", color: "white", border: "none", borderRadius: "3px" }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
