import React, { useEffect, useState } from "react";
import api from "../../api/api";
import AnalyticsCard from "../../components/AnalyticsCard";
import RatingList from "../../components/RatingList";

const AdminDashboard = () => {
  const [stats, setStats] = useState({ users: 0, ratings: 0, avgRating: 0 });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await api.get("/dashboard/admin/stats");
        setStats(res.data.data || { users: 0, ratings: 0, avgRating: 0 });
      } catch (err) {
        console.error("Error fetching admin stats:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchStats();
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="dashboard-container">
      <h2>Admin Dashboard</h2>
      <div className="analytics-grid">
        <AnalyticsCard title="Total Users" value={stats.users} />
        <AnalyticsCard title="Total Ratings" value={stats.ratings} />
        <AnalyticsCard title="Avg Rating" value={`${stats.avgRating.toFixed(1)} ⭐`} />
      </div>

      <h3>All Ratings</h3>
      <RatingList endpoint="/ratings/admin/all" canEdit canDelete />
    </div>
  );
};

export default AdminDashboard;
