import React, { useEffect, useState } from "react";
import api from "../../api/api";
import StoreCard from "../../components/StoreCard";
import RatingList from "../../components/RatingList";

const AdminDashboard = () => {
  const [stores, setStores] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchAdminData = async () => {
      try {
        const storeRes = await api.get("/stores/admin/all");
        const ratingRes = await api.get("/ratings/admin/all");
        const statsRes = await api.get("/dashboard/admin/stats");

        setStores(storeRes.data.data || []);
        setRatings(ratingRes.data.data || []);
        setStats(statsRes.data || {});
      } catch (err) {
        console.error("AdminDashboard fetch error:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchAdminData();
  }, []);

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>Admin Dashboard</h2>

      <div className="analytics">
        <p>Total Stores: {stats?.totalStores || stores.length}</p>
        <p>Total Ratings: {stats?.totalRatings || ratings.length}</p>
        <p>Average Rating: {stats?.avgRating?.toFixed(1) || 0} ‚≠ê</p>
      </div>

      <h3>All Stores</h3>
      <div className="store-list">
        {stores.map((store) => (
          <StoreCard
            key={store.id}
            store={store}
            onEdit={() => console.log("edit store", store)}
            onDelete={() => console.log("delete store", store)}
          />
        ))}
      </div>

      <h3>All Ratings</h3>
      <RatingList ratings={ratings} />
    </div>
  );
};

export default AdminDashboard;
