import React, { useState, useEffect } from "react";
import api from "../../api/api";

const UserDashboard = () => {
  const [stores, setStores] = useState([]);
  const [userRatings, setUserRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedStore, setSelectedStore] = useState(null);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");

  useEffect(() => {
    fetchStores();
    fetchUserRatings();
  }, []);

  const fetchStores = async () => {
    try {
      const response = await api.get("/stores");
      setStores(response.data.data);
    } catch (error) {
      console.error("Error fetching stores:", error);
    }
  };

  const fetchUserRatings = async () => {
    try {
      const response = await api.get("/ratings/user");
      setUserRatings(response.data.data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching user ratings:", error);
      setLoading(false);
    }
  };

  const submitRating = async (storeId) => {
    try {
      await api.post("/ratings", { storeId, rating: parseInt(rating), comment });
      setSelectedStore(null);
      setRating(5);
      setComment("");
      fetchStores(); // Refresh to get updated ratings
      fetchUserRatings();
    } catch (error) {
      console.error("Error submitting rating:", error);
      alert("Error submitting rating. You may have already rated this store.");
    }
  };

  const updateRating = async (ratingId) => {
    try {
      await api.put(`/ratings/${ratingId}`, { rating: parseInt(rating), comment });
      setSelectedStore(null);
      setRating(5);
      setComment("");
      fetchStores();
      fetchUserRatings();
    } catch (error) {
      console.error("Error updating rating:", error);
    }
  };

  const getUserRatingForStore = (storeId) => {
    return userRatings.find(r => r.storeId === storeId);
  };

  const renderStars = (rating) => {
    return "⭐".repeat(rating);
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>User Dashboard</h2>

      {/* Analytics Section */}
      <div style={{ display: "flex", gap: "20px", marginBottom: "30px" }}>
        <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "5px", flex: 1 }}>
          <h3>Total Stores</h3>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{stores.length}</p>
        </div>
        <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "5px", flex: 1 }}>
          <h3>My Ratings</h3>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>{userRatings.length}</p>
        </div>
        <div style={{ border: "1px solid #ccc", padding: "20px", borderRadius: "5px", flex: 1 }}>
          <h3>Average Rating Given</h3>
          <p style={{ fontSize: "24px", fontWeight: "bold" }}>
            {userRatings.length > 0 ? (userRatings.reduce((sum, r) => sum + r.rating, 0) / userRatings.length).toFixed(1) : 0} ⭐
          </p>
        </div>
      </div>

      {/* Rating Form Modal */}
      {selectedStore && (
        <div style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: "rgba(0,0,0,0.5)",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          zIndex: 1000
        }}>
          <div style={{ backgroundColor: "white", padding: "20px", borderRadius: "5px", width: "400px" }}>
            <h3>Rate {selectedStore.name}</h3>
            <div style={{ marginBottom: "15px" }}>
              <label>Rating:</label>
              <select value={rating} onChange={(e) => setRating(e.target.value)} style={{ marginLeft: "10px", padding: "5px" }}>
                {[1,2,3,4,5].map(num => <option key={num} value={num}>{num} ⭐</option>)}
              </select>
            </div>
            <div style={{ marginBottom: "15px" }}>
              <label>Comment:</label>
              <textarea
                value={comment}
                onChange={(e) => setComment(e.target.value)}
                style={{ width: "100%", height: "80px", padding: "5px" }}
                placeholder="Optional comment..."
              />
            </div>
            <div>
              <button
                onClick={() => {
                  const existingRating = getUserRatingForStore(selectedStore.id);
                  if (existingRating) {
                    updateRating(existingRating.id);
                  } else {
                    submitRating(selectedStore.id);
                  }
                }}
                style={{ marginRight: "10px", padding: "10px 20px", backgroundColor: "#28a745", color: "white", border: "none", borderRadius: "3px" }}
              >
                {getUserRatingForStore(selectedStore.id) ? "Update Rating" : "Submit Rating"}
              </button>
              <button
                onClick={() => setSelectedStore(null)}
                style={{ padding: "10px 20px", backgroundColor: "#6c757d", color: "white", border: "none", borderRadius: "3px" }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Stores Section */}
      <div>
        <h3>All Stores</h3>
        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(350px, 1fr))", gap: "20px" }}>
          {stores.map(store => {
            const userRating = getUserRatingForStore(store.id);
            return (
              <div key={store.id} style={{ border: "1px solid #ccc", padding: "15px", borderRadius: "5px" }}>
                <h4>{store.name}</h4>
                <p>{store.address}</p>
                <p>Owner: {store.owner?.name}</p>
                <p>Average Rating: {store.averageRating || 0} ⭐ ({store.totalRatings || 0} ratings)</p>

                {userRating ? (
                  <div style={{ backgroundColor: "#e9ecef", padding: "10px", borderRadius: "3px", marginBottom: "10px" }}>
                    <p><strong>Your Rating:</strong> {renderStars(userRating.rating)}</p>
                    <p><strong>Your Comment:</strong> {userRating.comment || "No comment"}</p>
                  </div>
                ) : null}

                <button
                  onClick={() => setSelectedStore(store)}
                  style={{
                    padding: "8px 16px",
                    backgroundColor: userRating ? "#007bff" : "#28a745",
                    color: "white",
                    border: "none",
                    borderRadius: "3px",
                    cursor: "pointer"
                  }}
                >
                  {userRating ? "Update Rating" : "Rate This Store"}
                </button>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
