import React, { useEffect, useState, useContext } from "react";
import api from "../../api/api";
import StoreCard from "../../components/StoreCard";
import { AuthContext } from "../../context/AuthContext";

const OwnerDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stores, setStores] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchOwnerData();
  }, []);

  const fetchOwnerData = async () => {
    try {
      const storeRes = await api.get("/stores/owner/my-stores");
      const statsRes = await api.get("/dashboard/owner/stats");

      setStores(storeRes.data.data || []);
      setStats(statsRes.data || {});
    } catch (err) {
      console.error("OwnerDashboard fetch error:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (storeId) => {
    if (!window.confirm("Are you sure you want to delete this store?")) return;
    try {
      await api.delete(`/stores/owner/${storeId}`);
      fetchOwnerData();
    } catch (err) {
      console.error("Error deleting store:", err);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>Owner Dashboard</h2>

      <div className="analytics">
        <p>My Stores: {stats?.totalStores || stores.length}</p>
        <p>Total Ratings: {stats?.totalRatings || 0}</p>
        <p>
          Average Rating: {stats?.avgRating?.toFixed(1) || 0} ⭐
        </p>
      </div>

      <h3>My Stores</h3>
      <div className="store-list">
        {stores.map((store) => {
          // ✅ fix: use store.ratings safely
          const userRating = store.ratings?.find(
            (r) => r.user?.id === user?.id
          );

          return (
            <StoreCard
              key={store.id}
              store={store}
              onEdit={() => console.log("Edit store", store)}
              onDelete={() => handleDelete(store.id)}
              onRate={() =>
                console.log(
                  userRating ? "Update Rating flow" : "New Rating flow"
                )
              }
            />
          );
        })}
      </div>
    </div>
  );
};


export default OwnerDashboard;
