import React, { useState, useEffect } from "react";
import api from "../../api/api";
import StarRating from "../../components/StarRating";
import RatingModal from "../../components/RatingModal";

const UserDashboard = () => {
  const [stores, setStores] = useState([]);
  const [userRatings, setUserRatings] = useState([]);
  const [loading, setLoading] = useState(true);

  const [selectedStore, setSelectedStore] = useState(null);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [storesRes, ratingsRes] = await Promise.all([
        api.get("/stores"),
        api.get("/ratings/user"),
      ]);
      setStores(storesRes.data.data);
      setUserRatings(ratingsRes.data.data);
    } catch (error) {
      console.error("Error fetching data:", error);
    } finally {
      setLoading(false);
    }
  };

  const submitRating = async (storeId) => {
    try {
      await api.post("/ratings", {
        storeId,
        rating: parseInt(rating),
        comment,
      });
      resetModal();
      fetchData();
    } catch (error) {
      console.error("Error submitting rating:", error);
      alert("Error submitting rating. You may have already rated this store.");
    }
  };

  const updateRating = async (ratingId) => {
    try {
      await api.put(`/ratings/${ratingId}`, {
        rating: parseInt(rating),
        comment,
      });
      resetModal();
      fetchData();
    } catch (error) {
      console.error("Error updating rating:", error);
    }
  };

  const resetModal = () => {
    setSelectedStore(null);
    setRating(5);
    setComment("");
  };

  const getUserRatingForStore = (storeId) => {
    return userRatings.find((r) => r.storeId === storeId);
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="dashboard-container">
      <h2>User Dashboard</h2>

      {/* Analytics Section */}
      <div className="analytics-grid">
        <div className="analytics-card">
          <h3>Total Stores</h3>
          <p>{stores.length}</p>
        </div>
        <div className="analytics-card">
          <h3>My Ratings</h3>
          <p>{userRatings.length}</p>
        </div>
        <div className="analytics-card">
          <h3>Average Rating Given</h3>
          <p>
            {userRatings.length > 0
              ? (
                  userRatings.reduce((sum, r) => sum + r.rating, 0) /
                  userRatings.length
                ).toFixed(1)
              : 0}{" "}
            ⭐
          </p>
        </div>
      </div>

      {/* Rating Modal */}
      <RatingModal
        store={selectedStore}
        rating={rating}
        comment={comment}
        setRating={setRating}
        setComment={setComment}
        onSubmit={() => {
          const existing = getUserRatingForStore(selectedStore.id);
          existing ? updateRating(existing.id) : submitRating(selectedStore.id);
        }}
        onClose={resetModal}
        isUpdating={!!getUserRatingForStore(selectedStore?.id)}
      />

      {/* Stores Section */}
      <div>
        <h3>All Stores</h3>
        <div className="stores-grid">
          {stores.map((store) => {
            const userRating = getUserRatingForStore(store.id);
            return (
              <div key={store.id} className="store-card">
                <h4>{store.name}</h4>
                <p>{store.address}</p>
                <p>Owner: {store.owner?.name}</p>
                <p>
                  Average Rating: {store.averageRating || 0} ⭐ (
                  {store.totalRatings || 0} ratings)
                </p>

                {userRating && (
                  <div className="user-rating-box">
                    <p>
                      <strong>Your Rating:</strong>{" "}
                      <StarRating rating={userRating.rating} />
                    </p>
                    <p>
                      <strong>Your Comment:</strong>{" "}
                      {userRating.comment || "No comment"}
                    </p>
                  </div>
                )}

                <button
                  onClick={() => setSelectedStore(store)}
                  className={userRating ? "btn btn-primary" : "btn btn-success"}
                >
                  {userRating ? "Update Rating" : "Rate This Store"}
                </button>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
