import React, { useEffect, useState } from "react";
import api from "../../api/api";
import StoreCard from "../../components/StoreCard";
import RatingList from "../../components/RatingList";

const UserDashboard = () => {
  const [stores, setStores] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    try {
      const storeRes = await api.get("/stores");
      const ratingRes = await api.get("/ratings/user");
      const statsRes = await api.get("/dashboard/user/stats");

      setStores(storeRes.data.data || []);
      setRatings(ratingRes.data.data || []);
      setStats(statsRes.data || {});
    } catch (err) {
      console.error("UserDashboard fetch error:", err);
    } finally {
      setLoading(false);
    }
  };

  const handleRate = async (store) => {
    const rating = parseInt(prompt("Enter rating (1-5):", "5"), 10);
    const comment = prompt("Enter comment:", "");
    if (!rating || rating < 1 || rating > 5) return alert("Invalid rating");

    try {
      await api.post("/ratings", { storeId: store.id, rating, comment });
      fetchUserData();
    } catch (err) {
      console.error("Error rating store:", err);
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div>
      <h2>User Dashboard</h2>

      <div className="analytics">
        <p>Total Stores: {stats?.totalStores || stores.length}</p>
        <p>My Ratings: {stats?.totalRatings || ratings.length}</p>
        <p>Average Rating Given: {stats?.avgRating?.toFixed(1) || 0} ‚≠ê</p>
      </div>

      <h3>My Ratings</h3>
      <RatingList ratings={ratings} />

      <h3>All Stores</h3>
      <div className="store-list">
        {stores.map((store) => (
          <StoreCard
            key={store.id}
            store={store}
            onRate={() => handleRate(store)}
          />
        ))}
      </div>
    </div>
  );
};

export default UserDashboard;
