import React, { useState, useEffect, useContext } from "react";
import api from "../../api/api";
import { normalizeResponse } from "../../utils/normalizeResponse";
import { AuthContext } from "../../context/AuthContext";

const OwnerDashboard = () => {
  const { user } = useContext(AuthContext);
  const [stores, setStores] = useState([]);
  const [loading, setLoading] = useState(true);

  // modal state
  const [selectedStore, setSelectedStore] = useState(null);
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [email, setEmail] = useState("");

  useEffect(() => {
    const fetchStores = async () => {
      try {
        const response = await api.get("/stores/owner/my-stores");
        setStores(normalizeResponse(response));
      } catch (error) {
        console.error("Error fetching stores:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchStores();
  }, [user?.id]);

  const createStore = async () => {
    try {
      await api.post("/stores/owner/create", { name, email, address });
      setName("");
      setEmail("");
      setAddress("");
      window.location.reload();
    } catch (error) {
      console.error("Error creating store:", error);
    }
  };

  const updateStore = async () => {
    try {
      await api.put(`/stores/owner/${selectedStore.id}`, {
        name,
        email,
        address,
      });
      setSelectedStore(null);
      window.location.reload();
    } catch (error) {
      console.error("Error updating store:", error);
    }
  };

  const deleteStore = async (id) => {
    try {
      await api.delete(`/stores/owner/${id}`);
      setStores((prev) => prev.filter((s) => s.id !== id));
    } catch (error) {
      console.error("Error deleting store:", error);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="dashboard-container">
      <h2>Owner Dashboard</h2>

      <div className="stores-grid">
        {stores.length > 0 ? (
          stores.map((store) => (
            <div key={store.id} className="store-card">
              <h4>{store.name}</h4>
              <p>{store.address}</p>
              <p>{store.email}</p>
              <button
                onClick={() => {
                  setSelectedStore(store);
                  setName(store.name);
                  setEmail(store.email);
                  setAddress(store.address);
                }}
                className="btn btn-primary"
              >
                Edit
              </button>
              <button
                onClick={() => deleteStore(store.id)}
                className="btn btn-danger"
              >
                Delete
              </button>
            </div>
          ))
        ) : (
          <p>No stores found.</p>
        )}
      </div>

      {/* Add Store Form */}
      <div className="form-section">
        <h3>Add Store</h3>
        <input
          placeholder="Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <input
          placeholder="Email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          placeholder="Address"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
        />
        <button onClick={createStore} className="btn btn-success">
          Add
        </button>
      </div>

      {/* Edit Modal */}
      {selectedStore && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Edit Store</h3>
            <input
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <input
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              placeholder="Address"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
            <button onClick={updateStore} className="btn btn-primary">
              Save
            </button>
            <button onClick={() => setSelectedStore(null)} className="btn">
              Cancel
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default OwnerDashboard;
