import React, { useState, useEffect } from "react";
import api from "../../api/api";
import StarRating from "../../components/StarRating";
import RatingModal from "../../components/RatingModal";

const AdminDashboard = () => {
  const [stores, setStores] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);

  const [selectedStore, setSelectedStore] = useState(null);
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [storesRes, ratingsRes] = await Promise.all([
          api.get("/stores/admin"),   // ✅ admin endpoint
          api.get("/ratings/admin"),  // ✅ admin endpoint
        ]);

        setStores(storesRes.data.data || []);
        setRatings(ratingsRes.data.data || []);
      } catch (error) {
        console.error("Error fetching admin data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const deleteStore = async (storeId) => {
    try {
      await api.delete(`/stores/${storeId}`);
      setStores((prev) => prev.filter((s) => s.id !== storeId));
    } catch (error) {
      console.error("Error deleting store:", error);
    }
  };

  const deleteRating = async (ratingId) => {
    try {
      await api.delete(`/ratings/${ratingId}`);
      setRatings((prev) => prev.filter((r) => r.id !== ratingId));
    } catch (error) {
      console.error("Error deleting rating:", error);
    }
  };

  const resetModal = () => {
    setSelectedStore(null);
    setRating(5);
    setComment("");
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="dashboard-container">
      <h2>Admin Dashboard</h2>

      {/* Rating Modal (reused) */}
      <RatingModal
        store={selectedStore}
        rating={rating}
        comment={comment}
        setRating={setRating}
        setComment={setComment}
        onSubmit={resetModal}
        onClose={resetModal}
        isUpdating={false}
      />

      {/* Stores Section */}
      <h3>All Stores</h3>
      <div className="stores-grid">
        {stores.length > 0 ? (
          stores.map((store) => (
            <div key={store.id} className="store-card">
              <h4>{store.name}</h4>
              <p>{store.address}</p>
              <p>Email: {store.email}</p>
              <p>
                Owner: {store.owner?.name || "N/A"} (
                {store.owner?.email || "N/A"})
              </p>
              <p>
                Average Rating: {store.averageRating ?? 0} ⭐ (
                {store.totalRatings ?? 0} ratings)
              </p>

              <button
                onClick={() => deleteStore(store.id)}
                className="btn btn-danger"
              >
                Delete Store
              </button>
            </div>
          ))
        ) : (
          <p>No stores found.</p>
        )}
      </div>

      {/* Ratings Section */}
      <h3>All Ratings</h3>
      <div className="ratings-grid">
        {ratings.length > 0 ? (
          ratings.map((r) => (
            <div key={r.id} className="rating-card">
              <p>
                <strong>User:</strong> {r.user?.name || "Unknown"} (
                {r.user?.email || "N/A"})
              </p>
              <p>
                <strong>Store:</strong> {r.store?.name || "Unknown"}
              </p>
              <p>
                <strong>Rating:</strong> <StarRating rating={r.rating} />
              </p>
              <p>
                <strong>Comment:</strong> {r.comment || "No comment"}
              </p>
              <p>
                <strong>Date:</strong>{" "}
                {new Date(r.createdAt).toLocaleDateString()}
              </p>

              <button
                onClick={() => deleteRating(r.id)}
                className="btn btn-danger"
              >
                Delete Rating
              </button>
            </div>
          ))
        ) : (
          <p>No ratings found.</p>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
