import React from "react";

const RatingModal = ({
  store,
  rating,
  comment,
  setRating,
  setComment,
  onSubmit,
  onClose,
  isUpdating,
}) => {
  if (!store) return null;

  return (
    <div
      style={{
        position: "fixed",
        top: 0,
        left: 0,
        right: 0,
        bottom: 0,
        backgroundColor: "rgba(0,0,0,0.5)",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        zIndex: 1000,
      }}
    >
      <div
        style={{
          backgroundColor: "white",
          padding: "20px",
          borderRadius: "5px",
          width: "400px",
        }}
      >
        <h3>{isUpdating ? "Update Rating" : "Rate"} {store.name}</h3>

        <div style={{ marginBottom: "15px" }}>
          <label>Rating:</label>
          <select
            value={rating}
            onChange={(e) => setRating(e.target.value)}
            style={{ marginLeft: "10px", padding: "5px" }}
          >
            {[1, 2, 3, 4, 5].map((num) => (
              <option key={num} value={num}>
                {num} ‚≠ê
              </option>
            ))}
          </select>
        </div>

        <div style={{ marginBottom: "15px" }}>
          <label>Comment:</label>
          <textarea
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            style={{ width: "100%", height: "80px", padding: "5px" }}
            placeholder="Optional comment..."
          />
        </div>

        <div>
          <button
            onClick={onSubmit}
            style={{
              marginRight: "10px",
              padding: "10px 20px",
              backgroundColor: "#28a745",
              color: "white",
              border: "none",
              borderRadius: "3px",
            }}
          >
            {isUpdating ? "Update" : "Submit"}
          </button>
          <button
            onClick={onClose}
            style={{
              padding: "10px 20px",
              backgroundColor: "#6c757d",
              color: "white",
              border: "none",
              borderRadius: "3px",
            }}
          >
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default RatingModal;
