import { normalizeResponse } from "../../utils/normalizeResponse";

useEffect(() => {
  const fetchStores = async () => {
    try {
      const response = await api.get("/stores");
      const storesData = normalizeResponse(response);

      // Only keep stores owned by logged-in owner
      setStores(storesData.filter((s) => s.owner?.id === user?.id));
    } catch (error) {
      console.error("Error fetching stores:", error);
    } finally {
      setLoading(false);
    }
  };

  if (user?.id) fetchStores();
}, [user?.id]);
