import React, { useState, useEffect } from "react";
import api from "../../api/api";
import { normalizeResponse } from "../../utils/normalizeResponse";
import StoreCard from "../../components/StoreCard";
import StarRating from "../../components/StarRating";

const AdminDashboard = () => {
  const [stores, setStores] = useState([]);
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);

  // edit modal state
  const [selectedStore, setSelectedStore] = useState(null);
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [storesRes, ratingsRes] = await Promise.all([
          api.get("/stores/admin/all"),
          api.get("/ratings/admin/all"),
        ]);
        setStores(normalizeResponse(storesRes));
        setRatings(normalizeResponse(ratingsRes));
      } catch (error) {
        console.error("Error fetching admin data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const updateStore = async () => {
    try {
      await api.put(`/stores/admin/${selectedStore.id}`, {
        name,
        email,
        address,
      });
      setSelectedStore(null);
      window.location.reload();
    } catch (error) {
      console.error("Error updating store:", error);
    }
  };

  const deleteStore = async (id) => {
    try {
      await api.delete(`/stores/admin/${id}`);
      setStores((prev) => prev.filter((s) => s.id !== id));
    } catch (error) {
      console.error("Error deleting store:", error);
    }
  };

  const deleteRating = async (id) => {
    try {
      await api.delete(`/ratings/${id}`);
      setRatings((prev) => prev.filter((r) => r.id !== id));
    } catch (error) {
      console.error("Error deleting rating:", error);
    }
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className="dashboard-container">
      <h2>Admin Dashboard</h2>

      {/* Stores Section */}
      <h3>All Stores</h3>
      <div className="stores-grid">
        {stores.length > 0 ? (
          stores.map((store) => (
            <StoreCard
              key={store.id}
              store={store}
              onEdit={(s) => {
                setSelectedStore(s);
                setName(s.name);
                setEmail(s.email);
                setAddress(s.address);
              }}
              onDelete={(s) => deleteStore(s.id)}
            />
          ))
        ) : (
          <p>No stores found.</p>
        )}
      </div>

      {/* Edit Modal */}
      {selectedStore && (
        <div className="modal-overlay">
          <div className="modal">
            <h3>Edit Store</h3>
            <input
              placeholder="Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <input
              placeholder="Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <input
              placeholder="Address"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
            <button onClick={updateStore} className="btn btn-primary">
              Save
            </button>
            <button onClick={() => setSelectedStore(null)} className="btn">
              Cancel
            </button>
          </div>
        </div>
      )}

      {/* Ratings Section */}
      <h3>All Ratings</h3>
      <div className="ratings-grid">
        {ratings.length > 0 ? (
          ratings.map((r) => (
            <div key={r.id} className="rating-card">
              <p>
                <strong>User:</strong> {r.user?.name} ({r.user?.email})
              </p>
              <p>
                <strong>Store:</strong> {r.store?.name}
              </p>
              <p>
                <strong>Rating:</strong> <StarRating rating={r.rating} />
              </p>
              <p>
                <strong>Comment:</strong> {r.comment}
              </p>
              <button
                onClick={() => deleteRating(r.id)}
                className="btn btn-danger"
              >
                Delete Rating
              </button>
            </div>
          ))
        ) : (
          <p>No ratings found.</p>
        )}
      </div>
    </div>
  );
};

export default AdminDashboard;
