import React, { useEffect, useState } from "react";
import api from "../../api/api";
import AnalyticsCard from "../../components/AnalyticsCard";
import StoreList from "../../components/StoreList";
import RatingList from "../../components/RatingList";

const UserDashboard = () => {
  const [stats, setStats] = useState({ ratings: 0, unrated: 0, avgRating: 0 });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await api.get("/dashboard/user/stats");
        setStats(res.data.data || { ratings: 0, unrated: 0, avgRating: 0 });
      } catch (err) {
        console.error("Error fetching user stats:", err);
      } finally {
        setLoading(false);
      }
    };
    fetchStats();
  }, []);

  if (loading) return <div>Loading...</div>;

  return (
    <div className="dashboard-container">
      <h2>User Dashboard</h2>
      <div className="analytics-grid">
        <AnalyticsCard title="My Ratings" value={stats.ratings} />
        <AnalyticsCard title="Unrated Stores" value={stats.unrated} />
        <AnalyticsCard title="Avg Rating Given" value={`${stats.avgRating.toFixed(1)} ⭐`} />
      </div>

      <h3>My Rated Stores</h3>
      <RatingList endpoint="/ratings/user" canEdit />

      <h3>All Stores</h3>
      <StoreList endpoint="/stores" canRate />
    </div>
  );
};

export default UserDashboard;
